package com.growcn.ce365.util;import com.growcn.ce365.util.AppConstant.Config;public class Log {	public static final boolean DEVELOPMENT_LOGGING_ENABLED = Config.DEVELOPMENT;	private static final boolean REMOTE_LOG = false;	public static void v(String tag, String msg) {		if (DEVELOPMENT_LOGGING_ENABLED && msg != null) {			android.util.Log.v(tag, msg);			TestFlightlog("d", tag, msg, REMOTE_LOG);		}	}	public static void d(String tag, String msg) {		if (DEVELOPMENT_LOGGING_ENABLED && msg != null) {			android.util.Log.d(tag, msg);			TestFlightlog("d", tag, msg, REMOTE_LOG);		}	}	public static void i(String tag, String msg) {		if (DEVELOPMENT_LOGGING_ENABLED && msg != null) {			android.util.Log.i(tag, msg);			TestFlightlog("i", tag, msg, REMOTE_LOG);		}	}	public static void w(String tag, String msg) {		if (DEVELOPMENT_LOGGING_ENABLED && msg != null) {			android.util.Log.w(tag, msg);			TestFlightlog("w", tag, msg, REMOTE_LOG);		}	}	public static void e(String tag, String msg) {		if (DEVELOPMENT_LOGGING_ENABLED && msg != null) {			android.util.Log.e(tag, msg);			TestFlightlog("e", tag, msg, REMOTE_LOG);		}	}	public static void TestFlightlog(String level, String tag, String msg,			Boolean remote_log) {	}}