package com.growcn.ce365.util;import com.growcn.ce365.activity.AboutActivity;import com.growcn.ce365.activity.DownLoadActivity;import com.growcn.ce365.activity.LessonActivity;import com.growcn.ce365.activity.ParagraphActivity;import com.growcn.ce365.activity.SettingActivity;import com.growcn.ce365.util.AppConstant.ActivityParams;import android.content.Context;import android.content.Intent;public class OpenIntent {	public static void inParagraphActivity(Context mContext, String LessonUid) {		Intent intent = new Intent(mContext, ParagraphActivity.class);		intent.putExtra(ActivityParams.UUID, LessonUid);		mContext.startActivity(intent);	}	public static void index(Context mContext) {		Intent intent = new Intent(mContext, LessonActivity.class);		mContext.startActivity(intent);	}	public static void about(Context mContext) {		Intent intent = new Intent(mContext, AboutActivity.class);		mContext.startActivity(intent);	}	public static void setting(Context mContext) {		Intent intent = new Intent(mContext, SettingActivity.class);		mContext.startActivity(intent);	}	public static void offlineDown(Context mContext) {		Intent intent = new Intent(mContext, DownLoadActivity.class);		mContext.startActivity(intent);	}}